import { useState } from 'react'
import Appbar from '../components/appbar'
import Terms from '../components/console_terms'
import { Typography } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { makeStyles } from '@material-ui/core/styles';


const handleChange = (event) => {
    setAge(event.target.value);
  };

  const useStyles = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));


export default function Console({ team }) {

    let teams = { team }
    console.log(teams.team.team)

    return (
        <div>
            <Select
                labelId="demo-simple-select-filled-label"
                id="demo-simple-select-filled"
                onChange={handleChange}
            >
                <MenuItem value="">
                    <em>None</em>
                </MenuItem>
                <MenuItem value={10}>Self</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>Thirty</MenuItem>
            </Select>
            <Terms></Terms>
        </div>
    )
}

const fetchData = async (query, auth) => await axios.get(`http://localhost:5050/api/console/${query.def}`, { headers: { auth: auth } })
    .then(res => ({
        team: res.data
    }))
    .catch(() => ({
        error: true
    }),
    );

export async function getServerSideProps(context) {

    let cookie = context.req.headers.cookie

    let auth = cookie.split('; ')
        .find(row => row.startsWith('auth'))
        .split('=')[1]

    const teams = await fetchData(context.query, auth);

    return {
        props: {
            team: teams
        }
    }

}
