import { useState, useEffect } from "react";
import Terms from "../components/console_terms";
import axios from "axios";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import jsCookie from "js-cookie";
import Head from "next/head";
import Typography from "@material-ui/core/Typography";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";

import Searchbar from "../components/search";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import Box from "@material-ui/core/Container";
import { useStyles } from "../utils/style";

export default function Console(team) {
  const classes = useStyles();
  let [teamSelected, setTeams] = useState([]);

  useEffect(() => {
    jsCookie.set("team", teamSelected);
  }, [teamSelected]);

  return (
    <>
      <Terms></Terms>
      <Head>
        <link
          rel="stylesheet"
          href="https://fonts.googleapis.com/icon?family=Material+Icons"
        />
      </Head>
      <Box className={classes.box}>
        <Card className={classes.root}>
          <CardActionArea>
            <CardMedia
              component="img"
              alt="Contemplative Reptile"
              height="140"
              image="/static/images/cards/contemplative-reptile.jpg"
              title="Contemplative Reptile"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
                Lizard
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                Lizards are a widespread group of squamate reptiles, with over
                6,000 species, ranging across all continents except Antarctica
              </Typography>
            </CardContent>
          </CardActionArea>
          <CardActions>
            <Button size="small" color="primary">
              Share
            </Button>
            <Button size="small" color="primary">
              Learn More
            </Button>
          </CardActions>
        </Card>
      </Box>
    </>
  );
}

const fetchData = async (query, auth) =>
  await axios
    .get(`/api/console/${query.def}`, { headers: { auth: auth } })
    .then((res) => ({
      team: res.data,
    }))
    .catch(() => ({
      error: true,
    }));

export async function getServerSideProps(context) {
  let cookie = context.req.headers.cookie;

  let auth = cookie
    .split("; ")
    .find((row) => row.startsWith("auth"))
    .split("=")[1];

  const teams = await fetchData(context.query, auth);

  return {
    props: {
      team: teams,
    },
  };
}
