import axios from 'axios';
import Link from 'next/Link';
import Wordcard from '../components/wordcard';
import Box from '@material-ui/core/Box';
import Card from '@material-ui/core/Card';
import { fade, makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({

  box: {

    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    marginTop: '5%',
    marginLeft: '20%',
    marginRight: '20%',
    textAlign: "center"
  },
  card: {

    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    marginTop: '5%',
    marginLeft: '20%',
    marginRight: '20%',
    textAlign: "center"
  }
}));

function gotToDef(word){
  console.log(word)
}

function listwords({ words }) {
  console.log(words)
  const classes = useStyles();
  return (
    <Box Container className={classes.box}>
    <div>
        {words.map(function (item, i) {

          return  <Card key={i} className={classes.card} onClick={gotToDef("ff")}> <Wordcard
                        word={item.word}
                        definition={item.definition}
            >
            </Wordcard>
            </Card>
        })}
    </div>
    </Box>
  )
}

export async function getServerSideProps() {

  let wordlist =  await axios.get(`http://localhost:5050/api/words`,{ headers: { 'Content-Type': 'application/json' } })

  return {
    props: {
      words: toJSON(wordlist)
    }
  }
}


export default listwords


