import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


export default function ButtonAppBar(props) {

  const classes = useStyles();

  const [loggedInUser, setLoggedInUser] = useState(props.user);
  const [loggedIn, setLoggedIn] = useState(props.loggedInState);

  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const loggedInStatus = () =>{

    setLoggedInUser(null)
    props.logout(true)

  }

  useEffect(() => {

    if (loggedInUser != null){
      setLoggedIn(true)
    }



  }, {...loggedInUser});

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
            <MenuIcon></MenuIcon>
          </Button>
          <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem onClick={loggedInStatus}>Logout</MenuItem>
          </Menu>

          <Typography variant="h6" className={classes.title}>
          </Typography>
          <Button href="../login" color="inherit">{loggedIn == false ? "Login" : "salah"} </Button>
        </Toolbar>
      </AppBar>
    </div>
  );
}
