import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { withCookies, Cookies } from 'react-cookie';
import { instanceOf } from 'prop-types';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  },
  appBar:{
    top: '0px',
    left: '0px',
    background: '#202020',
    border: '1px solid #707070',
    opacity: '1',
    position: "static"

  },

  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


export default function ButtonAppBar(props) {

  const classes = useStyles();

  const [loggedInUser, setLoggedInUser] = useState(null);
  const [loggedIn, setLoggedIn] = useState(false);

  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  let cookies = instanceOf(Cookies).isRequired

  console.log(props)

  const handleClose = () => {
    setAnchorEl(null);
  };

  const loggedInStatus = () =>{

    setLoggedInUser(null)

    props.logout(true)

  }

  useEffect(() => {

    setLoggedInUser(props.user)
    setLoggedIn(props.loggedInState)

}, []);


  return (
    <div className={classes.root}>
      <AppBar className={classes.appBar} >
        <Toolbar>
          <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
            <MenuIcon></MenuIcon>
          </Button>
          <Typography variant="h6" className={classes.title}>
          </Typography>

          {props.loggedInState == false ?
          <Button href="../login" color="inherit">Login</Button>
          :
          <>
            <Button href="../userprofile" color="inherit">{props.user}</Button>
            <Button href="../userprofile" color="inherit">{'Console'}</Button>
            <Button color="inherit"onClick={loggedInStatus}>Logout</Button>
          </>
          }
        </Toolbar>
      </AppBar>
    </div>
  );
}
