import Appbar from '../components/appbar';
import { useState, useEffect } from 'react'


function MyApp({ Component, pageProps }) {

    const handleLogout = () =>{
        setValue(localStorage.clear('loggedInUser'));
        setValue("");
    }

    const [loggIn, setValue] = useState(null);
    useEffect(() => {
        setValue(localStorage.getItem('loggedInUser'))
      },{...user});

    return (
            <>
        <Appbar user = {user} logout = {handleLogout} >   </Appbar>
            <Component {...pageProps} ></Component>
            </>

        )

  }

  export default MyApp
