import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import { fade, makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import { Typography } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: '0%',
    marginRight: '50%',
    marginLeft: '50%'
  },
  defInput:{
    top: '417px',
    left: '526px',
    width: '368px',
    height: '307px',
    backgroundColor: '#F0F4F8',
    borderRadius: '8px',
    opacity: '1'

  },
  addBtn:{

    width: '262px',
    height: '25px',
    marginLeft: '35%',
    background: '#202020 0% 0% no-repeat padding-box',
    borderRadius: '80px',
    opacity: '1'

  },
  termInput:{

    opacity: '1'

  },
  btn: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    width: '100%',
    justifyContent: 'center',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
  }
}));

export default function addwordform(props) {

  const classes = useStyles();
  const [ispreview, setPreview] = useState(false);
  const [all, setAll] = useState({ word: "", definition: "" });


  const sendPreview = (e) => {

    e.preventDefault();
    setPreview(true)

  }

  async function addWord(e) {

    e.preventDefault();
    props.returnData(all);
  }

  const cancelPreview = (e) => {

    e.preventDefault();
    setPreview(false)

  }

  const edit = (e) => {

    e.preventDefault();
    setPreview(false)

  }

  useEffect(() => {

    if (props.clear == true) {
      clear()
    }

  }, [props.clear])

  function clear() {

    setAll({ word: "", definition: "" });
  }


  return (

    <Box display="flex" 
    flexDirection= "column"
    justifyContent="center" 
    style = {{marginTop:'10%', marginLeft:'30%', marginRight:'30%'}}
    >
      {ispreview == false ?
        <>

          <TextField    name="word" 
                        variant="outlined" 
                        value={all.word} 
                        fullWidth
                        className={classes.termInput}
                        style = {{marginBottom:'5%'}} 
                        onChange={e => setAll({ ...all, [event.target.name]: event.target.value })} label="Word" />

          <TextField  name="definition" 
                      variant="outlined" 
                      classNam={classes.defInput}
                      value={all.definition} 

                      onChange={e => setAll({ ...all, [event.target.name]: event.target.value })} multiline rows={10} label="Definition" />

          <Button variant="outlined" className={classes.addBtn} style = {{width: '15%', marginTop:'2%'}}  onClick={sendPreview}  >
          <Typography>
            Preview
          </Typography>
         </Button>


        </>
        :

        <Container>
          <Typography>
            word:   {all.word}
               definition: {all.definition}
            <Button className={classes.btn} variant="outlined"  onClick={addWord} >
              add
              </Button>
            <Button className={classes.btn} variant="outlined"  onClick={cancelPreview} >
              cancel
            </Button>
            <Button className={classes.btn} variant="outlined"  onClick={edit} >
              Edit
            </Button>
          </Typography>
        </Container>
      }
     </Box>
  );
}
