import { useState } from 'react'
import Appbar from '../components/appbar'
import Terms from '../components/console_terms'
import { Typography } from '@material-ui/core';
import axios from 'axios';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { makeStyles } from '@material-ui/core/styles';
import Searchbar from '../components/search'
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import Box from '@material-ui/core/Box';
import { useStyles } from '../utils/style';
import Head from 'next/head'


const handleChange = (event) => {

};


export default function Console(team) {

    const classes = useStyles()
    //const classes = useStyles();

    let teamCol = team.team.team
    return (
        <div>
            <FormControl >
                <Typography>team </Typography><Select
                    labelId="demo-simple-select-filled-label"
                    id="demo-simple-select-filled"
                    onChange={handleChange}
                >
                    {teamCol.map((team, i) => {
                        return <MenuItem value={i}>{team.teams.team_name}</MenuItem>
                    })}
                </Select>
                <Head>
                    <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons" />
                </Head>
                <Box display="flex" css={{ maxWidth: 250 }} flexWrap="wrap" justifyContent="center" className={classes.box} >
                    <Searchbar></Searchbar><br />
                    <Button className={classes.btn} href={'./addword'} >
                        <Icon style={{ fontSize: 40 }} >add_circle</Icon>
                    </Button>
                </Box>
                <Terms></Terms>
            </FormControl>
        </div>
    )
}

const fetchData = async (query, auth) => await axios.get(`http://localhost:5050/api/console/${query.def}`, { headers: { auth: auth } })
    .then(res => ({
        team: res.data
    }))
    .catch(() => ({
        error: true
    }),
    );

export async function getServerSideProps(context) {

    let cookie = context.req.headers.cookie

    let auth = cookie.split('; ')
        .find(row => row.startsWith('auth'))
        .split('=')[1]

    const teams = await fetchData(context.query, auth);

    return {
        props: {
            team: teams
        }
    }

}
