import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import { fade, makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Popover from '@material-ui/core/Popover';
import Box from '@material-ui/core/Box';
import Modal from '@material-ui/core/Modal';
import Container from '@material-ui/core/Container';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: '0%',
    marginLeft: '0%',
    marginLeft: '50%'
  },
  wordtxt: {
    float: 'top',
    marginLeft: '10%',
    display: 'flex'
  },
  btn: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    width: '100%',
    justifyContent: 'center',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
  }
}));

export default function addwordform(props) {

  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [all, setAll] = useState({ word: "", definition: "" });

  const [clearText, setClear] = useState(props.clear)

  const sendWord = (e) => {

    e.preventDefault();

    props.returnData(all);

  }

  const cancelPrompt = (e) => {

    e.preventDefault();
  }

  useEffect(() => {

    if (props.clear == true) {
      clear()
    }

  }, [props.clear])

  function clear() {

    setAll({ word: "", definition: "" });
  }


  return (
    <div>
      <TextField id="standard-basic" name="word" value={all.word} onChange={e => setAll({ ...all, [event.target.name]: event.target.value })} label="Word" />
      <br />
      <TextField id="standard-basic" name="definition" value={all.definition} onChange={e => setAll({ ...all, [event.target.name]: event.target.value })} multiline rows={10} label="Definition" />
      <br />
      <Button className={classes.btn} onClick={sendWord}  >
        Add
      </Button>
      <Button className={classes.btn} onClick={sendWord}  >
        cancel
      </Button>
    </div>
  );
}
