import { useState, useEffect } from 'react'


export default function useLocalStorage (key, initialValue) {


    const [storedValue, setStoredValue] = useState(() => {

      try {

        const item = window.localStorage.getItem(key);

        return JSON.parse(item)

      } catch (error) {

        console.log(error);

        return initialValue;

      }

    });
}
