import { useState, useContext } from 'react'
import Addwordform from '../components/add'
import axios from 'axios';
import Router from 'next/router';
import { UserContext } from '../providers/userProvider';
import {useStyles} from '../utils/style';


export default function addword() {

  const [wordanddef, setWordandDef] = useState({ word: '', definition: '' });
  const [topics, setTopics] = useState([]);
  const user = useContext(UserContext);

  const getData = async (word) => {

    setWordandDef({ word: word.word, definition: word.definition, topics: topics })

    await axios.get(`/api/add`, { params: word }, { headers: { 'Content-Type': 'application/json' } })
      .then(function (response) {

        if (response.data.added == true) {
          Router.push('/congrats')
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  const getTopics= async (definition) => {

    await axios.get(`/api/topics/`, { params: definition }, { headers: { 'Content-Type': 'application/json' } })
      .then(function (response) {

        setTopics(response.data.result[0])

      })
      .catch(function (error) {
        console.log(error);
      });

      console.log(topics)
  }

  return (
      <Addwordform returnData={getData} returnTopics={getTopics} topics={topics}></Addwordform>
  )
}
