import { useRouter } from 'next/router'
import axios from 'axios';
import {withRouter} from 'next/router';
import { useState } from 'react';


function definitions ({definition, word}){

  return (<div><p>Word: {word} </p>  <p>definition: {definition}</p></div>)
}

const fetchData = async (query) => await axios.get(`http://localhost:5050/api/definition/${query.def}`)
  .then(res => ({
    definition: res.data
  }))
  .catch(() => ({
    error: true
  }),
  );

export async function getServerSideProps({query}) {


  const definition = await fetchData(query);
  console.log(definition)
  const word_definition = definition

   return {
     props: {
      definition: word_definition,
      word: query.def,
     }
   }
 }

export default definitions
