const userdata = (state = [], action) => {
  switch (action.type) {
    case "ADD_USER_DATA":
      console.log(action);
      return [
        ...state,
        {
          displayName: action.DisplayName,
          teams: action.teams,
          logged_in: true,
        },
      ];
    default:
      return state;
  }
};

export default userdata;
