      if (addedword.length != 0 & addedword.word != "") {

        let word = addedword.word
        let isAvailable = false

        if (word == 'a'|| word == 'b'){

          setClear(false)
          //console.log(shouldclear)
        }else{

           setClear(true)
           //console.log(shouldclear)
        }

