import React, { useState } from "react";
import Teamform from "./../components/addteamform";
import axios from "axios";
import { useDispatch } from "react-redux";
import { add_teams } from "../store/actions";

function doDispatch() {}

export default function Teams() {
  const [team, setTeam] = useState([]);
  const dispatch = useDispatch();
  dispatch(add_teams("test team 2"));

  async function getData(team) {
    setTeam({ team });
    doDispatch();

    let teams = await axios
      .get(
        `/api/teams/add`,
        { params: team },
        {
          headers: { "Content-Type": "application/json" },
          credentials: "include",
        }
      )
      .then(function (response) {
        dispatch(add_teams(response.data));
      })

      .catch(function (error) {
        console.log(error);
      });

    localStorage.setItem("teams", JSON.stringify(teams));
  }

  return <Teamform returnData={getData}></Teamform>;
}
