import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import { fade, makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import { Typography } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: '0%',
    marginLeft: '0%',
    marginLeft: '50%'
  },
  wordtxt: {
    float: 'top',
    marginLeft: '10%',
    display: 'flex'
  },
  btn: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    width: '100%',
    justifyContent: 'center',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
  }
}));

export default function addwordform(props) {

  const classes = useStyles();
  const [ispreview, setPreview] = useState(false);
  const [all, setAll] = useState({ word: "", definition: "" });


  const sendPreview = (e) => {

    e.preventDefault();
    setPreview(true)

  }

  async function addWord(e) {

    e.preventDefault();
    props.returnData(all);
  }

  const cancelPreview = (e) => {

    e.preventDefault();
    setPreview(false)

  }

  const edit = (e) => {

    e.preventDefault();
    setPreview(false)

  }


  useEffect(() => {

    if (props.clear == true) {
      clear()
    }

  }, [props.clear])

  function clear() {

    setAll({ word: "", definition: "" });
  }


  return (

    <Box display="flex" 
    flexDirection= "column"
    justifyContent="center" 
    style = {{marginTop:'10%', marginLeft:'35%'}}
    >
      {ispreview == false ?
        <>

          <TextField    name="word" 
                        variant="outlined" 
                        value={all.word} 
                        style = {{width: 250, marginBottom:'5%'}} 
                        onChange={e => setAll({ ...all, [event.target.name]: event.target.value })} label="Word" />
          <TextField  name="definition" 
                      variant="outlined" 
                      value={all.definition} 
                      style = {{width: 550}} 
                      onChange={e => setAll({ ...all, [event.target.name]: event.target.value })} multiline rows={10} label="Definition" />

          <Button variant="outlined"  style = {{width: 10, marginTop:'5%'}}  onClick={sendPreview}  >
            Preview
         </Button>

          <Button variant="outlined"  style = {{width: 10, marginTop:'5%'}}  >
            cancel
        </Button>


        </>
        :

        <Container>
          <Typography>
            word:   {all.word}
            <br />
               definition: {all.definition}
            <br />
            <Button className={classes.btn} variant="outlined"  onClick={addWord} >
              add
              </Button>
            <Button className={classes.btn} variant="outlined"  onClick={cancelPreview} >
              cancel
            </Button>
            <Button className={classes.btn} variant="outlined"  onClick={edit} >
              Edit
            </Button>
          </Typography>
        </Container>
      }
     </Box>
  );
}
