import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import { green } from '@material-ui/core/colors';
import Icon from '@material-ui/core/Icon';
import { fade, makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: '0%',
    marginLeft: '0%',
    marginLeft: '50%'
  },
  wordtxt: {
    float: 'top',
    marginLeft: '10%',
    display: 'flex'
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '0',
    margin: 0
  },

  paper: {
    backgroundColor: theme.palette.background.paper,
    width: '50%',
    height: '50%'
  },
  btn: {

    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    width: '100%',
    justifyContent: 'center',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
  }

}));

export default function ContainedButtons(props) {

  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [word, setWord] = useState("");
  const [definition, setDef] = useState("");
  const [all, setAll] = useState({ word: "", definition: "" });
  const [clearText, setClear] = useState(props.clear ? true : false)


  const handleOpen = () => {
    setOpen(true);
  };

  const sendWord = (e) => {

    e.preventDefault();

    props.returnData(all);

    //clear()

  }


  useEffect(() => {

    setClear(props.cleartext)
    console.log(clearText)
    if (props.cleartext == true){
      clear()
    }
  },[clear])

  function clear(){
    console.log("hh")
    setAll({ word: "", definition: "" });
  }

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <div className={classes.root}>
        <Button className={classes.btn} onClick={handleOpen} >
          <Icon style={{ fontSize: 40 }} >add_circle</Icon>
        </Button>
      </div>

      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>


          <div className={classes.paper} >
            <form noValidate autoComplete="off">
              <div className={classes.wordtxt} >

                <TextField id="standard-basic"  name="word" value = {all.word}  onChange={e => setAll({...all, [event.target.name]: event.target.value})} label="Word" />
                <br />
                <TextField id="standard-basic" name="definition" value = {all.definition} onChange={e => setAll({...all, [event.target.name]: event.target.value})} multiline rows={10} label="Definition" />
                <br />
                <Button className={classes.btn} onClick={sendWord}  >
                  Add
                </Button>

              </div>
            </form>
          </div>


        </Fade>
      </Modal>
    </div>
  );
}
