              console.error("Error writing document: ", error);
            })
        }*/
      }
    }

    sendtofb();
    setClear(shouldclear)




  }, [addedword]);



  return (
    <div>
      <Head>
        <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons" />
      </Head>
      <main>
        <Appbar></Appbar>
        <Searchbar></Searchbar>
        <Addbtn returnData={getData} clear = {clear} ></Addbtn>
      </main>
    </div>
  )
}
