import { useState, useEffect } from "react";
import Terms from "../components/console_terms";
import axios from "axios";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import jsCookie from "js-cookie";
import Head from "next/head";
import Typography from "@material-ui/core/Typography";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";

import Searchbar from "../components/search";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import Box from "@material-ui/core/Container";
import { useStyles } from "../utils/style";

export default function Console(team) {
  const classes = useStyles();
  let [teamSelected, setTeams] = useState([]);

  useEffect(() => {
    jsCookie.set("team", teamSelected);
  }, [teamSelected]);

  return (
    <>
      <Terms></Terms>
      <Box className={classes.box}>
        <Card style={{ width: "25%" }}>
          <CardActionArea>
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
                Add Word
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                Define a word that you think may benfifit yourself or those in
                your teams
              </Typography>
            </CardContent>
          </CardActionArea>
        </Card>
        <br />
        <Card style={{ width: "25%" }}>
          <CardActionArea>
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
                View Glossary
              </Typography>
              <Typography
                variant="body2"
                color="textSecondary"
                component="p"
              ></Typography>
            </CardContent>
          </CardActionArea>
        </Card>
        <br />
        <Card style={{ width: "25%" }}>
          <CardActionArea>
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
                Create a Team
              </Typography>
              <Typography
                variant="body2"
                color="textSecondary"
                component="p"
              ></Typography>
            </CardContent>
          </CardActionArea>
        </Card>
      </Box>
    </>
  );
}

const fetchData = async (query, auth) =>
  await axios
    .get(`/api/console/${query.def}`, { headers: { auth: auth } })
    .then((res) => ({
      team: res.data,
    }))
    .catch(() => ({
      error: true,
    }));

export async function getServerSideProps(context) {
  let cookie = context.req.headers.cookie;

  let auth = cookie
    .split("; ")
    .find((row) => row.startsWith("auth"))
    .split("=")[1];

  const teams = await fetchData(context.query, auth);

  return {
    props: {
      team: teams,
    },
  };
}
