import React, {useState} from "react";
import Link  from "next/Link";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { Typography } from '@material-ui/core';

const SignIn = (props) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);

    const signInWithEmailAndPasswordHandler = (event,email, password) => {

        event.preventDefault();
        console.log(email)
        props.Signin(email, password);

      };

      const onChangeHandler = (event) => {
          const {name, value} = event.currentTarget;

          if(name === 'userEmail') {
              setEmail(value);
          }
          else if(name === 'userPassword'){
            setPassword(value);
          }
      };


  return (

    <div>
      <Typography>Sign In</Typography>
         <TextField id="outlined-basic" label="Email" onChange = {(event) => onChangeHandler(event)} variant="outlined" />
         <br/>
         <TextField id="outlined-basic" label="password"     onChange = {(event) => onChangeHandler(event)} variant="outlined" />
         <br/>
          <Button variant="contained" color="primary" onClick = {(event) => {signInWithEmailAndPasswordHandler(event, email, password)}}>
           Log On
            </Button>
          <br/>
          <p className="text-center my-3">
          Don't have an account?
          <Link href="/signup" as="/signup">Sign up here</Link>
        </p>
    </div>

  );
};

export default SignIn;
