      .then(res =>  res )
      .catch(() => ({
        error: true,
        wordlist: null}));
        console.log(response.data.userinfo)
        getuserinfo(response.data.userinfo)

    }

  useEffect(()=>{

    fetchData()

  },userinfo)

  return (
    <Profile props={userinfo}></Profile>
  )




}



export default UserProfile
