import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


export default function ButtonAppBar(props) {

  const classes = useStyles();

  const [loggedIn, setLoginState] = useState('Login');
  const [anchorEl, setAnchorEl] = useState(null);


const handleClick = (event) => {
  setAnchorEl(event.currentTarget);
};

const handleClose = () => {
  setAnchorEl(null);
};

  useEffect(() => {
    setLoginState(props.user)
    console.log(loggedIn)
  },loggedIn);



  return (
    <div className={classes.root}>
      <AppBar  position="static">
        <Toolbar>
          <IconButton onClick={handleClick} id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)} onClose={handleClose} edge="start" aria-controls="simple-menu" aria-haspopup="true" className={classes.menuButton} color="inherit" aria-label="menu">
            <MenuIcon />

        <MenuItem onClick={handleClose}>Profile</MenuItem>
        <MenuItem onClick={handleClose}>My account</MenuItem>
        <MenuItem onClick={handleClose}>Logout</MenuItem>



          </IconButton>
          <Typography variant="h6" className={classes.title}>
          </Typography>
          <Button href="../login" color="inherit">{loggedIn} </Button>
        </Toolbar>
      </AppBar>
    </div>
  );
}
