import SignIn from '../components/signin'
import axios from 'axios';
import { useContext } from 'react';
import { UserContext } from '../providers/userProvider'
import router from 'next/router';

export default function Signin() {

    const { user, setUser } = useContext(UserContext);

    const signInWithEmailAndPassword = async (email, password) => {

        try {
            const useruid = await axios.get(`/api/auth/signin`, { params: { email, password } }, { headers: { 'Content-Type': 'application/json' } })
                .then(response => response)

            if (useruid.data.userInfo.uid.length > 0) {
                localStorage.setItem('loggedInUser', useruid.data.userInfo.displayName);
                router.reload()
                router.push('/')
            }
        }
        catch (error) {
        }
    };

    return (
        <div>
            <SignIn Signin={signInWithEmailAndPassword} ></SignIn>
        </div>
    )

}
