import axios from 'axios';
import Link from 'next/Link'
import {useState} from 'react'

 function listwords({ words }) {

  const [word, getWords] = useState([])
  getWords(words)

    return (
        <div>
        {words.tempDoc.map(function(item, i){

             return  <Link  href={{pathname: '/definitions', query: {id: item.word}}} as={`/definitions/${item.word}`}><a>{item.word}</a></Link>
            })}
      </div>
      )
}

export async function getServerSideProps(context) {





  console.log(context.query.word)

   const wordlist = await axios.get(`http://localhost:5050/api/words`)

    return {
      props: {
       words: wordlist.data
      }
    }
  }


export default listwords
