import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Link from "next/link";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import { useSelector } from "react-redux";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  appBar: {
    top: "0px",
    left: "0px",
    background: "#202020",
    border: "1px solid #707070",
    opacity: "1",
    position: "static",
  },
  rightside: {
    flexGrow: 1,
    marginRight: theme.spacing(1),
    primary: {
      main: "white",
    },
  },
  leftside: {
    marginLeft: theme.spacing(2),
  },

  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));

export default function ButtonAppBar(props) {
  const classes = useStyles();

  const [loggedInUser, setLoggedInUser] = useState(null);
  const [avialableTeams, setTeams] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);

  const loggedInStatus = () => {
    setLoggedInUser(null);

    props.logout(true);
  };

  const userdata = useSelector((state) => state);

  const handleChange = () => {};

  const renderMenuItems = () => {
    if (avialableTeams.length != 0) {
    }
  };

  const ButtonLink = ({ className, href, hrefAs, children }) => (
    <Link href={href} as={hrefAs} shallow={true}>
      <a className={className}>{children}</a>
    </Link>
  );

  useEffect(() => {
    let displayName = localStorage.getItem("displayName");
    let teams = localStorage.getItem("teams");
    let teamList = JSON.parse(teams);

    setLoggedInUser(displayName);
    setTeams(teamList);
    setLoggedIn(props.loggedInState);
  }, []);

  return (
    <div className={classes.root}>
      <AppBar className={classes.appBar}>
        <Toolbar>
          <Typography className={classes.rightside}>
            <Link
              style={{ textDecoration: "none", color: "white" }}
              underline="none"
              href="./"
            >
              Palabra
            </Link>
          </Typography>

          {props.loggedInState == false ? (
            <Typography>
              <Link
                className={classes.leftside}
                style={{ textDecoration: "none", padding: "5em" }}
                underline="none"
                href="../login"
              >
                Login
              </Link>
              <Link
                className={classes.leftside}
                style={{ textDecoration: "none", color: "white" }}
                underline="none"
                href="../signup"
              >
                Sign Up
              </Link>
            </Typography>
          ) : (
            <>
              <Select
                style={{ background: "white", width: "10%", height: "10%" }}
                variant={"outlined"}
                onChange={handleChange}
              >
                {avialableTeams.map((teams) => {
                  return (
                    <MenuItem
                      selected
                      style={{ backgroundColor: "white" }}
                      value={teams}
                    >
                      {teams}
                    </MenuItem>
                  );
                })}
              </Select>
              <Button
                component={ButtonLink}
                href={"userprofile"}
                color="inherit"
                hrefAs={"userprofile"}
              >
                {props.user}
              </Button>
              <Button
                component={ButtonLink}
                href={"console"}
                hrefAs={"console"}
                color="inherit"
              >
                {"Console"}
              </Button>
              <Button color="inherit" onClick={loggedInStatus}>
                Logout
              </Button>
            </>
          )}
        </Toolbar>
      </AppBar>
    </div>
  );
}

