import SignIn from "../components/signin";
import axios from "axios";
import router from "next/router";
import Card from "@material-ui/core/Card";
import Box from "@material-ui/core/Box";
import { makeStyles } from "@material-ui/core/styles";
import { connect, useDispatch } from "react-redux";
import { add_user_data } from "../store/actions";

const useStyles = makeStyles((theme) => ({
  box: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    marginTop: "5%",
    marginLeft: "20%",
    marginRight: "20%",
    textAlign: "center",
  },
  card: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    marginTop: "5%",
    marginLeft: "20%",
    marginRight: "20%",
    textAlign: "center",
  },
}));

function Signin() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const signInWithEmailAndPassword = async (email, password) => {
    try {
      const useruid = await axios
        .get(
          `/api/auth/signin`,
          { params: { email, password } },
          { headers: { "Content-Type": "application/json" } }
        )
        .then((response) => response);
      console.log(useruid.data);
      dispatch(
        add_user_data(useruid.data.userInfo.displayName, useruid.data.teams)
      );
      if (useruid.data.userInfo.uid.length > 0) {
        /*router.push({
          pathname: "/console",
          query: {
            data: useruid.data.userInfo.displayName,
            teams: useruid.data.teams,
          },
        });
        router.reload(); */
      }
    } catch (error) {}
  };

  return (
    <div>
      <Box Container className={classes.box}>
        <Card>
          <SignIn Signin={signInWithEmailAndPassword}></SignIn>
        </Card>
      </Box>
    </div>
  );
}

export default connect()(Signin);
