import { UserContext } from '../providers/userProvider';
import Appbar from '../components/appbar';
import { useState, useEffect } from 'react'
import Router from 'next/router';

// Hook


function MyApp({ Component, pageProps }) {

    const handleLogout = () => {

        setLoggedUser(localStorage.clear('loggedInUser'));
        setLoggedUser("");
        setStatus([false]);
    }

    const [user, setUser] = useLocalStorage('loggedInUser', '');

    const [loggedinuser, setLoggedUser] = useState(user);

    const [loggedin, setStatus] = useState(false);

    useEffect(() => {

        const user  = localStorage.getItem('loggedInUser');
        console.log(user)
        if (user != null){

            setLoggedUser(user);
            setStatus(true);
        }

    }, [loggedinuser,loggedin]);

    useEffect(() => {

        const user  = localStorage.getItem('loggedInUser');
        console.log(user)
        if (user != null){

            setLoggedUser(user);
            setStatus(true);
        }

    }, [loggedinuser]);


    return (
        <>
            <Appbar user={loggedinuser} loggedInState={loggedin} logout={handleLogout} >   </Appbar>
            <Component {...pageProps} ></Component>
        </>

    )

}


export  const useLocalStorage = (key, initialValue)  =>{

    // State to store our value

    // Pass initial state function to useState so logic is only executed once

    const [storedValue, setStoredValue] = useState(() => {

      try {

        // Get from local storage by key

        const item = window.localStorage.getItem(key);

        // Parse stored json or if none return initialValue

        return item ? JSON.parse(item) : initialValue;

      } catch (error) {

        // If error also return initialValue

        console.log(error);

        return initialValue;

      }

    });
}

export default MyApp
