import { useState, useContext} from 'react'
import Addwordform from '../components/add'
import axios from 'axios';
import Router from 'next/router';
import {UserContext} from '../providers/userProvider';

export default function addword() {

  const [wordanddef ,setWordandDef] = useState({word:'', definition:''});
  const user = useContext(UserContext);

  const getData = async (word) => {

    setWordandDef({word:word.word, definition:word.definition})
    console.log(word)

    await axios.get(`/api/add`,{params:word} , { headers: { 'Content-Type': 'application/json' } })
      .then(function (response) {

        console.log(response)

        if(response.data.added == true)
        {
          const {pathname} = Router
             Router.push('/congrats')
        }

      })
      .catch(function (error) {
        console.log(error);
      });
}

  return (

    <div>
      <Addwordform returnData={getData} >{JSON.stringify({user})}</Addwordform>
    </div>

  )
}
