import Head from 'next/head'
import Appbar from '../components/appbar'
import Searchbar from '../components/search'
import Addbtn from '../components/addword'
import react, { useEffect, useState } from 'react'
import { firestore } from "../utils/firebase"


export default function Home() {

  const [addedword, setWord] = useState([])
  const [clear, setClear] = useState(null)

  const getData = async (word) => {

    var clear  = false
    setClear(clear)
    setWord(word)

  }

  useEffect(() => {

    let shouldclear = false;

    async function sendtofb() {

      if (addedword.length != 0 & addedword.word != "") {

        let word = addedword.word
        console.log(word)
        let isAvailable = false

        if (word == 'a' || word == 'b'){
          setClear(shouldclear)
          //console.log(shouldclear)
        }else{
           shouldclear = true;
           //console.log(shouldclear)
        }

     /*
       await firestore.collection("words").doc(word).get().then(function (doc) {
          if (doc.exists) {
            isAvailable = true
            setClear(false)


          }else{
            let shouldclear = true;

            console.log(shouldclear)
          }
        }).catch(function (error) {
          console.log("Error getting document:", error);
        }); */


/*
        if (isAvailable == false) {

          await firestore.collection("words").doc(word).set({
            definition: addedword.definition,
            industry: "Construction",
            sub_industry: "Construction",
            user: "Salah",
            word: addedword.word
          })
            .then(function () {
              console.log("Document successfully written!");


            })
            .catch(function (error) {
              console.error("Error writing document: ", error);
            })
        }*/
      }
    }

    sendtofb();
    setClear(shouldclear)




  }, [addedword]);



  return (
    <div>
      <Head>
        <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons" />
      </Head>
      <main>
        <Appbar></Appbar>
        <Searchbar></Searchbar>
        <Addbtn returnData={getData} clear = {clear} ></Addbtn>
      </main>
    </div>
  )
}
