import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { withCookies, Cookies } from 'react-cookie';
import { instanceOf } from 'prop-types';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  },
  appBar:{
    top: '0px',
    left: '0px',
    background: '#202020',
    border: '1px solid #707070',
    opacity: '1',
    position: "static"

  },
  rightside:{

    flexGrow: 1,
    marginRight: theme.spacing(2),

  },
  leftside:{

  },

  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


export default function ButtonAppBar(props) {

  const classes = useStyles();

  const [loggedInUser, setLoggedInUser] = useState(null);
  const [loggedIn, setLoggedIn] = useState(false);

  const [anchorEl, setAnchorEl] = useState(null);

  const handleClose = () => {
    setAnchorEl(null);
  };

  const loggedInStatus = () =>{

    setLoggedInUser(null)

    props.logout(true)

  }

  useEffect(() => {

    setLoggedInUser(props.user)
    setLoggedIn(props.loggedInState)

}, []);


  return (
    <div className={classes.root}>
      <AppBar className={classes.appBar} >
        <Toolbar>
          <Typography href="./" variant="h6" className={classes.rightside}>
            Palabra 
          </Typography>
          {props.loggedInState == false ?
          <Button className={classes.leftside} href="../login" color="inherit">Login</Button>
          :
          <>
            <Button href="../userprofile" color="inherit">{props.user}</Button>
            <Button href="../console" color="inherit">{'Console'}</Button>
            <Button color="inherit"onClick={loggedInStatus}>Logout</Button>
          </>
          }
        </Toolbar>
      </AppBar>
    </div>
  );
}
