import React, {useState} from "react";
import Link  from "next/Link";
import TextField from '@material-ui/core/TextField';

const SignIn = (props) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);

    const signInWithEmailAndPasswordHandler = (event,email, password) => {

        event.preventDefault();
        console.log(email)
        props.Signin(email, password);

      };

      const onChangeHandler = (event) => {
          const {name, value} = event.currentTarget;

          if(name === 'userEmail') {
              setEmail(value);
          }
          else if(name === 'userPassword'){
            setPassword(value);
          }
      };


  return (

    <div>
         <TextField id="outlined-basic" label="Email" onChange = {(event) => onChangeHandler(event)} variant="outlined" />
         <br/>
         <TextField id="outlined-basic" label="password"     onChange = {(event) => onChangeHandler(event)} variant="outlined" />
         <button className="bg-green-400 hover:bg-green-500 w-full py-2 text-white" onClick = {(event) => {signInWithEmailAndPasswordHandler(event, email, password)}}>

            Sign in
          </button>
          <p className="text-center my-3">
          Don't have an account?
          <Link href="/signup" as="/signup">Sign up here</Link>
        </p>
    </div>

  );
};

export default SignIn;
