import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import { fade, makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import { Typography } from '@material-ui/core';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';


const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: '0%',
    marginRight: '50%',
    marginLeft: '50%'
  },
  defInput:{

    width: '368px',
    height: '307px',
    backgroundColor: '#F0F4F8',
    borderRadius: '8px',
    opacity: '1'

  },
  preview:{

    width: '105px',
    height: '25px',
    background: '#202020 0% 0% no-repeat padding-box',
    borderRadius: '80px',
    opacity: '1',
    display: "flex",
    justifyContent: "center",
    margin:'auto',
    marginTop:'5%'


  },
  card:{
    display: 'flex',
    flexDirection: 'column',
  },
  termInput:{

    opacity: '1',
    marginBottom:'5%'

  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 24,
  },
  pos: {
    marginBottom: 24,
  },
}));

export default function addwordform(props) {

  const classes = useStyles();
  const [ispreview, setPreview] = useState(false);
  const [all, setAll] = useState({ word: "", definition: "" });


  const sendPreview = (e) => {

    e.preventDefault();
    setPreview(true)

  }

  async function addWord(e) {

    e.preventDefault();
    props.returnData(all);
  }

  const cancelPreview = (e) => {

    e.preventDefault();
    setPreview(false)

  }

  const edit = (e) => {

    e.preventDefault();
    setPreview(false)

  }

  useEffect(() => {

    if (props.clear == true) {
      clear()
    }

  }, [props.clear])

  function clear() {

    setAll({ word: "", definition: "" });
  }


  return (

    <Box display="flex" 
    flexDirection= "column"
    justifyContent="center" 
    m={20} 

    >
      {ispreview == false ?
        <>

          <TextField    name="word" 
                        variant="outlined" 
                        value={all.word} 
                        fullWidth
                        className={classes.termInput}
                        onChange={e => setAll({ ...all, [event.target.name]: event.target.value })} label="Word" />

          <TextField  name="definition" 
                      variant="outlined" 
                      classNam={classes.defInput}
                      value={all.definition} 
                      onChange={e => setAll({ ...all, [event.target.name]: event.target.value })} multiline rows={10} label="Definition" />

          <Button variant="outlined" className={classes.preview} onClick={sendPreview}  >
          <Typography style={{color: 'white'}} >
            Preview
          </Typography>
         </Button>


        </>
        :
           <Card className={classes.card}>
            <CardContent  >
              <Typography className={classes.title} color="textSecondary" gutterBottom>
                Word
              </Typography>
              <Typography variant="h5" component="h2">
              {all.word}
              </Typography>
              <br/>
              <Typography className={classes.pos} color="textSecondary">
                Definition
              </Typography>
              <Typography variant="body2" component="p">
              {all.definition}
              </Typography>
            </CardContent>
            <CardActions>
            <Button className={classes.preview} variant="outlined"  onClick={addWord} >
            add
            </Button>
          <Button className={classes.preview} variant="outlined"  onClick={cancelPreview} >
            cancel
          </Button>
          <Button className={classes.preview} variant="outlined"  onClick={edit} >
            Edit
          </Button>
            </CardActions>
          </Card>
      }
     </Box>
  );
}
